          user={user}
          getPostDetails={getPostDetails}
        />
      </div>
    )
  }

  return (
    <div class="flex flex-wrap">
      {postList &&
        postList.map((posts, index) => (
          <div class="w-full md:w-1/2 lg:w-1/3 p-2">
            <div class="bg-white rounded shadow">
              <Link to={`/PostDetails/${posts.id}`}>
                <img
                  src={posts.image}
                  alt="post image"
                  class="w-full rounded-t"
                />
              </Link>
              <div class="p-4">
                <p class="text-lg font-semibold mb-2">
                  {posts.fishType} {posts.fishSpecies} {posts.fishSize}
                  <br></br>
                  <button
                    className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-green-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                    onClick={addComment}
                  >
                    Add Comment
                  </button>
                </p>
              </div>
            </div>
          </div>
        ))}
    </div>
  )
}

export default Post
